import React from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
// import moment from 'react-moment';

export default class MembersActive extends React.Component{

    constructor(props){
        super(props);
        this.state={
            dateArray:[],
            display:false,
            stTime:"",
            endTime:""

        }
    }

    handleChange(event){
       const selectedDate = event.toDateString();
       let activeDate;
       console.log(selectedDate);
       let startTime;
       let eTime;
        this.props.period.forEach(e =>
            { 
               startTime =  e.start_time.split("  ");
               eTime = e.end_time.split(" ").pop();
               activeDate = new Date(startTime[0]).toDateString(); // to convert date to a to a more readable format

               if(activeDate === selectedDate){
                   this.setState({
                        display: true,
                        stTime:startTime[1],
                        endTime:eTime
                   })
               }
            }
        )
        console.log(this.state.endTime);
        console.log(this.state.stTime);
    }

    render(){
        return(
            <section>
                <h3>{this.props.name}</h3>
                <p>{this.props.id}</p>
                <p>{this.props.index}</p>
                <p>Active Duration:</p>
                {
                    this.state.display && 
                    <div>
                        <p>{this.state.stTime}</p>  <p>{this.state.endTime}</p>
                    </div>

                }
                {
                    !this.state.display && 
                    <p>Please Select different date to view active duration</p>
                }

                <DatePicker
                    selected={new Date()}
                    maxDate={new Date()}
                    showMonthDropdown
                    showYearDropdown
                    className="DatePicker"
                    placeholderText="Click to select a date"
                    onChange={this.handleChange.bind(this)}
                />
                <button className="btn" onClick={this.props.togglePopup}>CLOSE</button>
            </section>
        );
    }
}
