import React from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

export default class MembersActive extends React.Component{

    constructor(props){
        super(props);
        this.state={
            dateArr:[]
        }
    }

    createDateArray(){
        const dateArr=[];

        if(this.props.period && this.props.period.length>0){
                this.props.period.forEach((e,index)=> {
                    dateArr[index]=e.start_time.split("  ")[0]  
                    console.log(dateArr);  
                 }); 
                 this.setState({
                    dateArr:dateArr
                 })
        }
        console.log(this.state.dateArr);
    }

    render(){
        return(
            <section>
                <h3>{this.props.name}</h3>
                <p>{this.props.id}</p>
                <p>{this.props.index}</p>
                <p onClick={this.createDateArray.bind(this)}>Active Duration:</p>
                { 
                    this.props.period.map((members,index) =>{
                        return(
                            <span key={index}>
                                <div>Start Time:{members.start_time}</div>
                                <div>End Time:{members.end_time}</div>
                            </span>
                        );
                    })
                } 
                <DatePicker
                    selected={new Date()}
                    showTimeSelect
                    maxDate={new Date()}
                    showMonthDropdown
                    showYearDropdown
                    placeholderText="Click to select a date"
                    // minTime={setHours(setMinutes(new Date(), 0), 17)}
                    // maxTime={setHours(setMinutes(new Date(), 30), 20)}
                />
                <button onClick={this.props.togglePopup}>CLOSE</button>
            </section>
        );
    }
}
