import React from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
// import moment from 'react-moment';

export default class MembersActive extends React.Component{

    constructor(props){
        super(props);
        this.state={
            dateArray:[],
            display:false,
            stTime:"",
            endTime:""

        }
    }

    handleChange(event){
       const selectedDate = event.toDateString();
       let activeDate;
       console.log(selectedDate);
       let startTime;
       let eTime;
        this.props.period.forEach(e =>
            { 
               startTime =  e.start_time.split("  ");
               eTime = e.end_time.split(" ").pop();
               activeDate = new Date(startTime[0]).toDateString();
               console.log(eTime);
               console.log(startTime);

            }
        )

    }

    render(){
        return(
            <section>
                <h3>{this.props.name}</h3>
                <p>{this.props.id}</p>
                <p>{this.props.index}</p>
                <p onClick={()=>this.createDateArray()}>Active Duration:</p>
                { 
                    this.props.period.map((members,index) =>{
                        return(
                            <span key={index}>
                                <div>Start Time:{members.start_time}</div>
                                <div>End Time:{members.end_time}</div>
                            </span>
                        );
                    })
                } 
                <DatePicker
                    selected={new Date()}
                    // showTimeSelect
                    maxDate={new Date()}
                    showMonthDropdown
                    showYearDropdown
                    placeholderText="Click to select a date"
                    onChange={this.handleChange.bind(this)}
                />
                <button onClick={this.props.togglePopup}>CLOSE</button>
            </section>
        );
    }
}
