import React from 'react';
import logo from './logo.svg';
import PopUp from './Component/PopUp/PopUp';
import './App.css';

class App extends React.Component{
  constructor(){
    super();
    this.state={
      opnDesk: false
    }
    this.toggleDesk = this.toggleDesk.bind(this);
  }

  toggleDesk(){
    this.setState({
      opnDesk:true
    });
  }

  render(){
    return (
      <div className="App">
        <button onClick={this.toggleDesk}>Show Desktop</button>
        {
          this.state.opnDesk && 
          <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
        }

      </div>
    );
  }
}

export default App;
