import React from 'react';
import {submitCampaign} from '../../Services/member-services';
import Popup from '../PopUp/PopUp';
import './Members.css';

export default class Members extends React.Component{
    constructor(){
        super();
        this.state={
           members:[],
           opnPopup:"false",

        }
    }
    componentDidMount(){
      this.fetchSubmitCampaign();
    }

    fetchSubmitCampaign(){
        submitCampaign()
        .then(response => response.json())
        .then(data =>{
            if(data){
               this.setState({
                   members: data[0].members
               })
               console.log(data);
               console.log(this.state.members);
            }
        })
        .catch(error =>{
            console.log("ERROR");
        })
    }

    togglePopup(index,period){
        this.setState({
            opnPopup:"true"
        });
    }

    render(){
        return(
            <main className="wrapper">
                <section>
                    <table className="member">
                        <thead>
                            <tr>
                                <th>Members ID</th>
                                <th>Members Name</th>
                                <th>Members Location</th>
                                <th>Members Active Detail</th>
                            </tr>
                        </thead>
                        <tbody>

                                {this.state.members && this.state.members.length>0 &&
                                    this.state.members.map((item,index)=>{
                                        return(
                                            <tr key={index}>
                                                <td>{item.id}</td>
                                                <td>{item.real_name}</td>
                                                <td>{item.tz}</td>
                                                <td>
                                                    <button onClick={()=>this.togglePopup(index,item.activity_periods)}>View</button>
                                                </td>
                                            </tr>
                                         );
                                        })      
                                }                    
                        </tbody>
                    </table>
                </section>
                {  this.state.opnPopup &&

                  <Popup title="Active Period" togglePopup={this.togglePopup.bind(this)}/>

                }          
            </main>
        );
    }
}
