import React from 'react';
import {submitCampaign} from '../../Services/member-services';
import Popup from '../PopUp/PopUp';
import './Members.css';
import Activity from '../MembersActive/MembersActive';

export default class Members extends React.Component{
    constructor(){
        super();
        this.state={
           members:[],
           opnPopup:false,
            name:"",
            period:"",
            id:""

        }
        this.togglePopup = this.togglePopup.bind(this);
    }
    componentDidMount(){
      this.fetchSubmitCampaign();
    }

    fetchSubmitCampaign(){
        submitCampaign()
        .then(response => response.json())
        .then(data =>{
            if(data){
               this.setState({
                   members: data[0].members
               })
               console.log(data);
               console.log(this.state.members);
            }
        })
        .catch(error =>{
            console.log("ERROR");
        })
    }

    viewAction(name,period,id,event){
        console.log(event);
       this.setState({
               name:name,
               period:period,
               id:id
       });
       console.log(this.state.member);
    }

    togglePopup(){
        const toggle = this.state.opnPopup;
        this.setState({
            opnPopup:!toggle
        });
    }

    render(){
        return(
            <main className="wrapper">
                <section>
                    <table className="member">
                        <thead>
                            <tr>
                                <th>Members ID</th>
                                <th>Members Name</th>
                                <th>Members Location</th>
                                <th>Members Active Detail</th>
                            </tr>
                        </thead>
                        <tbody>

                                {this.state.members && this.state.members.length>0 &&
                                    this.state.members.map((item,index)=>{
                                        console.log(index);
                                        return(
                                            <tr key={index}>
                                                <td>{item.id}</td>
                                                <td>{item.real_name}</td>
                                                <td>{item.tz}</td>
                                                <td>
                                                    <button onClick={this.togglePopup,()=>this.viewAction(item.real_name,item.activity_periods,item.id)}>View</button>
                                                     {  this.state.opnPopup &&
                                                        <Popup title="Active Period" togglePopup={this.togglePopup}>
                                                            <Activity
                                                               period={item.activity_periods}
                                                               id={item.id}
                                                               index={index}
                                                               name={item.real_name}
                                                               togglePopup={this.togglePopup}
                                                            />
                                                        </Popup>  
                                                    }   
                                                </td>
                                            </tr>
                                         );
                                        })      
                                }                    
                        </tbody>
                    </table>
                </section>

            </main>
        );
    }
}
